package program.com;

public class pattern3 {
 
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print("*");
			}
			System.out.println();
		}
		for(int x=1;x<=5;x++)
		{									
			for(int y=x;y<=5;y++)		
			{						
				System.out.print("*");
			}	
				System.out.println();
		}
	}

}
